.tc-chooser-item svg,
.tc-chooser-item img{
	width: 1em;
	height: 1em;
	vertical-align: middle;
}

.tc-language-chooser .tc-image-button img {
	width: 2em;
	vertical-align: -0.15em;
}

/*
** Palette swatches
*/

.tc-swatches-horiz {
}

.tc-swatches-horiz .tc-swatch {
	display: inline-block;
}

.tc-swatch {
	width: 2em;
	height: 2em;
	margin: 0.4em;
	border: 1px solid #888;
}

input.tc-palette-manager-colour-input {
	width: 100%;
	padding: 0;
}

/*
** Table of contents
*/

.tc-sidebar-lists .tc-table-of-contents {
	white-space: nowrap;
}

.tc-table-of-contents button,
.tc-table-of-contents .toc-item-muted {
	color: <<colour sidebar-foreground>>;
}

.tc-table-of-contents svg {
	width: 0.7em;
	height: 0.7em;
	vertical-align: middle;
	fill: <<colour sidebar-foreground>>;
}

.tc-table-of-contents ol {
	list-style-type: none;
	padding-left: 0;
}

.tc-table-of-contents ol ol {
	padding-left: 1em;
}

.tc-table-of-contents li {
	font-size: 1.0em;
	font-weight: bold;
}

.tc-table-of-contents li a {
	font-weight: bold;
}

.tc-table-of-contents li li {
	font-size: 0.95em;
	font-weight: normal;
	line-height: 1.4;
}

.tc-table-of-contents li li a {
	font-weight: normal;
}

.tc-table-of-contents li li li {
	font-size: 0.95em;
	font-weight: normal;
	line-height: 1.5;
}

.tc-table-of-contents li li li li {
	font-size: 0.95em;
	font-weight: normal;
}

.tc-tabbed-table-of-contents {
	display: -webkit-flex;
	display: flex;
}

.tc-tabbed-table-of-contents .tc-table-of-contents {
	z-index: 100;
	display: inline-block;
	padding-left: 1em;
	max-width: 50%;
	-webkit-flex: 0 0 auto;
	flex: 0 0 auto;
	background: <<colour tab-background>>;
	border-left: 1px solid <<colour tab-border>>;
	border-top: 1px solid <<colour tab-border>>;
	border-bottom: 1px solid <<colour tab-border>>;
}

.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item > a,
.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item-selected > a {
	display: block;
	padding: 0.12em 1em 0.12em 0.25em;
}

.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item > a {
	border-top: 1px solid <<colour tab-background>>;
	border-left: 1px solid <<colour tab-background>>;
	border-bottom: 1px solid <<colour tab-background>>;
}

.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item > a:hover {
	text-decoration: none;
	border-top: 1px solid <<colour tab-border>>;
	border-left: 1px solid <<colour tab-border>>;
	border-bottom: 1px solid <<colour tab-border>>;
	background: <<colour tab-border>>;
}

.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item-selected > a {
	border-top: 1px solid <<colour tab-border>>;
	border-left: 1px solid <<colour tab-border>>;
	border-bottom: 1px solid <<colour tab-border>>;
	background: <<colour background>>;
	margin-right: -1px;
}

.tc-tabbed-table-of-contents .tc-table-of-contents .toc-item-selected > a:hover {
	text-decoration: none;
}

.tc-tabbed-table-of-contents .tc-tabbed-table-of-contents-content {
	display: inline-block;
	vertical-align: top;
	padding-left: 1.5em;
	padding-right: 1.5em;
	border: 1px solid <<colour tab-border>>;
	-webkit-flex: 1 0 50%;
	flex: 1 0 50%;
}

/*
** Dirty indicator
*/

html body svg.tc-image-save-button-dynamic .tc-image-save-button-dynamic-clean {
	visibility: visible;
}

html body svg.tc-image-save-button-dynamic .tc-image-save-button-dynamic-dirty {
	visibility: hidden;
}

html body.tc-dirty svg.tc-image-save-button-dynamic .tc-image-save-button-dynamic-clean {
	visibility: hidden;
}

html body.tc-dirty svg.tc-image-save-button-dynamic .tc-image-save-button-dynamic-dirty {
	visibility: visible;
}

html body.tc-dirty span.tc-dirty-indicator, html body.tc-dirty span.tc-dirty-indicator svg {
	fill: <<colour dirty-indicator>>;
	color: <<colour dirty-indicator>>;
}

/*
** File inputs
*/

.tc-file-input-wrapper {
	position: relative;
	overflow: hidden;
	display: inline-block;
	vertical-align: middle;
}

.tc-file-input-wrapper input[type=file] {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	font-size: 999px;
	max-width: 100%;
	max-height: 100%;
	filter: alpha(opacity=0);
	opacity: 0;
	outline: none;
	background: white;
	cursor: pointer;
	display: inline-block;
}

::-webkit-file-upload-button {
	cursor:pointer;
}

/*
** Thumbnail macros
*/

.tc-thumbnail-wrapper {
	position: relative;
	display: inline-block;
	margin: 6px;
	vertical-align: top;
}

.tc-thumbnail-right-wrapper {
	float:right;
	margin: 0.5em 0 0.5em 0.5em;
}

.tc-thumbnail-image {
	text-align: center;
	overflow: hidden;
	border-radius: 3px;
}

.tc-thumbnail-image svg,
.tc-thumbnail-image img {
	filter: alpha(opacity=1);
	opacity: 1;
	min-width: 100%;
	min-height: 100%;
	max-width: 100%;
}

.tc-thumbnail-wrapper:hover .tc-thumbnail-image svg,
.tc-thumbnail-wrapper:hover .tc-thumbnail-image img {
	filter: alpha(opacity=0.8);
	opacity: 0.8;
}

.tc-thumbnail-background {
	position: absolute;
	border-radius: 3px;
}

.tc-thumbnail-icon svg,
.tc-thumbnail-icon img {
	width: 3em;
	height: 3em;
	<<filter "drop-shadow(2px 2px 4px rgba(0,0,0,0.3))">>
}

.tc-thumbnail-wrapper:hover .tc-thumbnail-icon svg,
.tc-thumbnail-wrapper:hover .tc-thumbnail-icon img {
	fill: #fff;
	<<filter "drop-shadow(3px 3px 4px rgba(0,0,0,0.6))">>
}

.tc-thumbnail-icon {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	display: -webkit-flex;
	-webkit-align-items: center;
	-webkit-justify-content: center;
	display: flex;
	align-items: center;
	justify-content: center;
}

.tc-thumbnail-caption {
	position: absolute;
	background-color: #777;
	color: #fff;
	text-align: center;
	bottom: 0;
	width: 100%;
	filter: alpha(opacity=0.9);
	opacity: 0.9;
	line-height: 1.4;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}

.tc-thumbnail-wrapper:hover .tc-thumbnail-caption {
	filter: alpha(opacity=1);
	opacity: 1;
}

/*
** Diffs
*/

.tc-diff-equal {
	background-color: <<colour diff-equal-background>>;
	color: <<colour diff-equal-foreground>>;
}

.tc-diff-insert {
	background-color: <<colour diff-insert-background>>;
	color: <<colour diff-insert-foreground>>;
}

.tc-diff-delete {
	background-color: <<colour diff-delete-background>>;
	color: <<colour diff-delete-foreground>>;
}

.tc-diff-invisible {
	background-color: <<colour diff-invisible-background>>;
	color: <<colour diff-invisible-foreground>>;
}

.tc-diff-tiddlers th {
	text-align: right;
	background: <<colour background>>;
	font-weight: normal;
	font-style: italic;
}

.tc-diff-tiddlers pre {
	margin: 0;
	padding: 0;
	border: none;
	background: none;
}

/*
** Errors
*/

.tc-error {
	background: #f00;
	color: #fff;
}

/*
** Tree macro
*/

.tc-tree div {
	padding-left: 14px;
}

.tc-tree ol {
	list-style-type: none;
	padding-left: 0;
	margin-top: 0;
}

.tc-tree ol ol {
	padding-left: 1em;
}

.tc-tree button {
	color: #acacac;
}

.tc-tree svg {
	fill: #acacac;
}

.tc-tree span svg {
	width: 1em;
	height: 1em;
	vertical-align: baseline;
}

.tc-tree li span {
	color: lightgray;
}

select {
	color: <<colour select-tag-foreground>>;
	background: <<colour select-tag-background>>;
}

/*
** Translink macro
*/

.tc-translink {
	background-color: <<colour pre-background>>;
	border: 1px solid <<colour pre-border>>;
	padding: 0 3px;
	border-radius: 3px;
}

div.tc-translink > div {
	margin: 1em;
}

div.tc-translink > div > a:first-child > h1 {
	font-size: 1.2em;
	font-weight: bold;
}

span.tc-translink > a:first-child {
	font-weight: bold;
}

/*
** Classes for displaying globals
*/

.tc-global-tiddler-body {
	padding: 0.25em;
	border: 1px solid <<colour foreground>>;
	background-color: <<colour muted-foreground>>;
	border-radius: 3px;
}

.tc-global-tiddler-body-heading {
	margin: 0 0 0.25em 0;
	font-weight: normal;
}

.tc-global-tiddler-body-type {
	margin: 0 0 0.25em 0;
	border-bottom: 1px solid <<colour foreground>>;
}

.tc-global-tiddler-body-details {
	background-color: <<colour background>>;
}

.tc-global-tiddler-body pre {
	margin: 0;
	border: 1px solid <<colour foreground>>;
}

/*
** Utility classes for SVG icons
*/

.tc-fill-background {
	fill: <<colour background>>;
}

.tc-network-activity-background {
	fill: <<colour network-activity-foreground>>;
}

/*
** Test Cases
*/

.tc-test-case-wrapper {
	border: 1px solid <<colour foreground>>;
	background-color: <<colour muted-foreground>>;
	border-radius: 6px;
}

.tc-test-case-wrapper {
	background-color: <<colour testcase-accent-level-1>>;
}

.tc-test-case-wrapper .tc-test-case-wrapper {
	background-color: <<colour testcase-accent-level-2>>;
}

.tc-test-case-wrapper .tc-test-case-wrapper .tc-test-case-wrapper {
	background-color: <<colour testcase-accent-level-3>>;
}

.tc-test-case-header {
	font-weight: normal;
	margin: 0.5em 0;
	padding: 0 0.5em;
}

.tc-test-case-divider {
	x-background-color: <<colour muted-foreground>>;
}

.tc-test-case-result-icon {
	fill: #fff;
	padding: 0.25em;
	display: inline-block;
	line-height: 0;
	border-radius: 1em;
	vertical-align: text-bottom;
	margin-right: 0.25em;
}

.tc-test-case-result-icon-pass {
	background-color: green;
}

.tc-test-case-result-icon-fail {
	background-color: red;
}

.tc-test-case-result-icon svg {
	width: 0.5em;
	height: 0.5em;
}

.tc-test-case-header > h2 {
	background: <<colour background>>;
	border-radius: 4px;
	padding: 0.25em;
}

.tc-test-case-header > h2,
.tc-test-case-source > pre {
	margin: 0;
}

.tc-test-case-header > h2 a.tc-tiddlylink-missing {
	font-style: normal;
}

.tc-test-case-toolbar {
	float: right;
}

.tc-test-case-toolbar svg {
	fill: <<colour tiddler-controls-foreground>>;
}

.tc-test-case-toolbar .tc-drop-down {
	font-size: 0.8em;
}

.tc-test-case-result-fail {
	border: 1px solid <<colour foreground>>;
	background-color: <<colour background>>;
	border-radius: 4px;
	margin: 0 0.5em;
	padding: 0;
}

.tc-test-case-result-fail-header {
	background: <<colour diff-delete-background>>;
	color: <<colour diff-delete-foreground>>;
	border-top-left-radius: 4px;
	border-top-right-radius: 4px;
	padding: 4px;
}

.tc-test-case-result-fail-body {
	padding: 4px;
}

.tc-test-case-source > pre {
	height: 100%;
}

.tc-test-case-narrative {
	padding: 0.5em;
}

.tc-test-case-panes {
	display: flex;
	align-items: stretch;
	flex-wrap: wrap;
	padding: 0.5em;
	border-bottom-left-radius: 6px;
	border-bottom-right-radius: 6px;
}

.tc-test-case-source {
	flex: 1 0 49%;
	min-width: 250px;
}

.tc-test-case-source .tc-tab-content {
	background: inherit;
	margin: 0;
}

.tc-test-case-source .tc-tab-content .tc-field-table {
	background: <<colour background>>;
}

.tc-test-case-source .tc-field-table {
	width: 100%;
}

.tc-test-case-source table.tc-field-table {
	margin: 0;
}

.tc-test-case-source .tc-tiddler-frame .tc-edit-texteditor {
	margin: 0;
}

.tc-test-case-divider {
	flex: 0 0 1.5%;
}

.tc-test-case-source .tc-tab-buttons {
	padding-top: 0;
}

.tc-test-case-footer-toolbar {
	display: flex;
	justify-content: flex-end;
}

.tc-test-case-output {
	box-shadow: inset 2px 2px 10px 0px <<colour muted-foreground>>;
	background: <<colour background>>;
	border-radius: 4px;
	border: 1px solid <<colour muted-foreground>>;
	flex: 1 0 49%;
	min-width: 250px;
	padding: 0.25em 1em;
}

/*
** Flexbox utility classes
*/

.tc-flex {
	display: -webkit-flex;
	display: flex;
}

.tc-flex-column {
	flex-direction: column;
}

.tc-flex-row {
	flex-direction: row;
}

.tc-flex-grow-1 {
	flex-grow: 1;
}

.tc-flex-grow-2 {
	flex-grow: 2;
}

/*
** Other utility classes
*/

/* Horizontal gaps */

.tc-tiny-gap {
	margin-left: .25em;
	margin-right: .25em;
}

.tc-tiny-gap-left {
	margin-left: .25em;
}

.tc-tiny-gap-right {
	margin-right: .25em;
}

.tc-small-gap {
	margin-left: .5em;
	margin-right: .5em;
}

.tc-small-gap-left {
	margin-left: .5em;
}

.tc-small-gap-right {
	margin-right: .5em;
}

.tc-big-gap {
	margin-left: 1em;
	margin-right: 1em;
}

.tc-big-gap-left {
	margin-left: 1em;
}

.tc-big-gap-right {
	margin-right: 1em;
}

.tc-word-break {
	word-break: break-all;
}

/* Vertical gaps */

.tc-tiny-v-gap-bottom {
	margin-bottom: 3px;
}

